css:
.topcoat-button,
.topcoat-button--quiet,
.topcoat-button--large,
.topcoat-button--large--quiet,
.topcoat-button--cta,
.topcoat-button--large--cta {
  position: relative;
  display: inline-block;
  vertical-align: top;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  -webkit-background-clip: padding;
  -moz-background-clip: padding;
  background-clip: padding-box;
  padding: 0;
  margin: 0;
  font: inherit;
  color: inherit;
  background: transparent;
  border: none;
  cursor: default;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -o-text-overflow: ellipsis;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  padding: 0 1.16rem;
  font-size: 12px;
  line-height: 2rem;
  letter-spacing: 1px;
  color: #c6c8c8;
  text-shadow: 0 -1px rgba(0,0,0,0.69);
  vertical-align: top;
  background-color: #595b5b;
  -webkit-box-shadow: inset 0 1px rgba(255,255,255,0.12);
  box-shadow: inset 0 1px rgba(255,255,255,0.12);
  border: 1px solid rgba(0,0,0,0.36);
  -webkit-border-radius: 3px;
  border-radius: 3px;
}position: relativedisplay: inline-blockvertical-align: top-webkit-box-sizing: border-box-moz-box-sizing: border-boxbox-sizing: border-box-webkit-background-clip: padding-moz-background-clip: paddingbackground-clip: padding-boxpadding: 0margin: 0font: inheritcolor: inheritbackground: transparentborder: nonecursor: default-webkit-user-select: none-moz-user-select: none-ms-user-select: noneuser-select: none-o-text-overflow: ellipsistext-overflow: ellipsiswhite-space: nowrapoverflow: hiddenpadding: 0 1.16remfont-size: 12pxline-height: 2remletter-spacing: 1pxcolor: #c6c8c8text-shadow: 0 -1px rgba(0,0,0,0.69)vertical-align: topbackground-color: #595b5b-webkit-box-shadow: inset 0 1px rgba(255,255,255,0.12)box-shadow: inset 0 1px rgba(255,255,255,0.12)border: 1px solid rgba(0,0,0,0.36)-webkit-border-radius: 3pxborder-radius: 3px.topcoat-button:active,
.topcoat-button.is-active,
.topcoat-button--large:active,
.topcoat-button--large.is-active {
  background-color: #404141;
  -webkit-box-shadow: inset 0 1px rgba(0,0,0,0.18);
  box-shadow: inset 0 1px rgba(0,0,0,0.18);
}background-color: #404141-webkit-box-shadow: inset 0 1px rgba(0,0,0,0.18)box-shadow: inset 0 1px rgba(0,0,0,0.18).topcoat-button:disabled,
.topcoat-button.is-disabled {
  opacity: 0.3;
  cursor: default;
  pointer-events: none;
}opacity: 0.3cursor: defaultpointer-events: none

.topcoat-button--quiet {
  background: transparent;
  border: 1px solid transparent;
  -webkit-box-shadow: none;
  box-shadow: none;
